function checkPassword(){var e=document.getElementById("password").value,s=document.getElementById("length"),c=document.getElementById("lowercase"),t=document.getElementById("uppercase"),n=document.getElementById("number"),a=document.getElementById("special");checkIfEightChar(e)?s.classList.add("success"):s.classList.remove("success"),checkIfOneLowercase(e)?c.classList.add("success"):c.classList.remove("success"),checkIfOneUppercase(e)?t.classList.add("success"):t.classList.remove("success"),checkIfOneDigit(e)?n.classList.add("success"):n.classList.remove("success"),checkIfOneSpecialChar(e)?a.classList.add("success"):a.classList.remove("success")}function getPassword(){document.getElementById("password").value;$(".checklist-password").show()}function checkIfEightChar(e){return e.length>=8}function checkIfOneLowercase(e){return/[a-z]/.test(e)}function checkIfOneUppercase(e){return/[A-Z]/.test(e)}function checkIfOneDigit(e){return/[0-9]/.test(e)}function checkIfOneSpecialChar(e){return/[~`!#$%\^&*+=\-\[\]\\';,/{}|\\":<>\?]/g.test(e)}function alertCheckPassword(){$(".checklist-password").removeClass("is-invalid").addClass("is-invalid")}